$('.newUserlink').tapend(function(ev){

	var endY = ev.pageY || ev.originalEvent.changedTouches[0].pageY;
	if(Math.abs(startTap.Y - endY) < 10){
		$("#login , .menu-main").hide();
		$("#appHolder").show();
	
		back.push("Login");
	newUserScroll =  new IScroll('.newUSerWrap', { preventDefault: false, probeType: 3, mouseWheel: true });  
	}
	

});

$('#first-time .btnFull').tapend(function(){
	if(checkEmail($('#first-time input[type="email"]').eq(0).val())){
		if($('#first-time input[type="password"]').eq(0).val().length > 8){
			if($('#first-time input[type="password"]').eq(0).val() != $('#first-time input[type="password"]').eq(1).val()){
				showInfoD($.t('Wrong data'),$.t('Passwords do not match'),function(){$('.moldHide, .dialogAlert').hide();});
			}else{
			
				var patt = /^\d{4}$/;
				
				if( patt.test($('#first-time input[type="tel"]').eq(0).val())){
					if($("#first-time .weeksOption input:checked").length == 1){
					var pass= HexWhirlpool($('#first-time input[type="password"]').eq(0).val());
					$.post('http://'+IP+':'+PORT+'/appriz/createNewUser',{
							email 		: $('#first-time input[type="email"]').eq(0).val(),
							password	: pass,
							retention   : parseInt($("#first-time .weeksOption input:checked").val()),//parseInt($('.unikOptions .active').attr('weeks')),
							pinPolicy   : $("#pinPolicyFT").prop('checked') ? 0 : 1,
							pin 		: $('#first-time input[type="tel"]').eq(0).val(),
							phoneDevice :  typeof device !== 'undefined' ? device.model : "Browser",
							so 			: typeof device !== 'undefined' ? device.platform : "Browser",
							pushKey		: PN,
							uuid		: typeof device !== 'undefined' ? device.uuid : "Browser"
						},function(data){
							if(data["status"] == 200){
								$.jStorage.set('idSecretClient', data['idSecretClient']);
								idScretClient = data['idSecretClient'];
								console.log(JSON.stringify(data));
								showInfoD($.t('Appriz User Created'),$.t('The user was created successfully'),
								
								function(){
									location.reload(); 
									
									/*var patFemail = /(\S+)@/;
									logAs = $('#first-time input[type="email"]').eq(0).val().match(patFemail)[1];
									$.jStorage.set('logAs', logAs);
									$('.user div').html($.jStorage.get('logAs'));
									checkWithOutEntity();
									
									$('.moldHide, .dialogAlert').hide();
									$('#suscribe input').eq(0).val("");
									$('#suscribe input').eq(1).val("");
									$('#Suscri').hide();
									$('.icon-menu').show();
									$('.icon-back').show();
									$('.out').hide();*/
									
									
										
								}
								);
							}else{
								showInfoD($.t("Error"),$.t("Email was taken"),function(){},function(){});
							}
						});
					}else{
						showInfoD($.t('Error'),$.t('You need to select a retention policy'),function(){$('.moldHide, .dialogAlert').hide();});
					}
				}else{
					showInfoD($.t('Wrong Accses Code'),$.t('PIN must be of fourth digits'),function(){$('.moldHide, .dialogAlert').hide();});
				}
				
			}
		}else{
			showInfoD($.t('Wrong password'),$.t('The password must be at least 9 chars length'),function(){$('.moldHide, .dialogAlert').hide();});
		}
	}else{
		showInfoD($.t('Wrong Email'),$.t('This email is not valid'),function(){$('.moldHide, .dialogAlert').hide();});
	}
});


